CREATE TABLE forum_answer (id BIGINT AUTO_INCREMENT, text TEXT NOT NULL, authorname BIGINT NOT NULL, question_id BIGINT NOT NULL, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, INDEX question_id_idx (question_id), PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = INNODB;
CREATE TABLE forum_category (id BIGINT AUTO_INCREMENT, name VARCHAR(255) NOT NULL, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = INNODB;
CREATE TABLE forum_questions (id BIGINT AUTO_INCREMENT, text TEXT NOT NULL, author_id BIGINT NOT NULL, category_id BIGINT NOT NULL, title VARCHAR(50) NOT NULL, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, INDEX category_id_idx (category_id), INDEX author_id_idx (author_id), PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = INNODB;
CREATE TABLE forum_users (id BIGINT AUTO_INCREMENT, username VARCHAR(255) NOT NULL UNIQUE, password VARCHAR(255) NOT NULL, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(id)) ENGINE = INNODB;
ALTER TABLE forum_answer ADD CONSTRAINT forum_answer_question_id_forum_questions_id FOREIGN KEY (question_id) REFERENCES forum_questions(id) ON DELETE CASCADE;
ALTER TABLE forum_questions ADD CONSTRAINT forum_questions_category_id_forum_category_id FOREIGN KEY (category_id) REFERENCES forum_category(id) ON DELETE CASCADE;
ALTER TABLE forum_questions ADD CONSTRAINT forum_questions_author_id_forum_users_id FOREIGN KEY (author_id) REFERENCES forum_users(id) ON DELETE CASCADE;
